import React,{ Component } from 'react';

class TextCounter extends Component{
    constructor(props){
        super(props);
        this.limit=10;
        this.state={
            totalChars:0
        }
    }
    render(){
        const{state}=this;
        return(
            <div>
                <h1>Meu Contator</h1>
                <textarea />
                <div>
                    <strong>Total:</strong>{state.totalChars}
                </div>
            </div>
        );
    }
}


export default TextCounter;
