                        {
                            this.fruits.map(fruit => <option value={fruit.value}>{frui}</option>)
                        }
                    </select>
                </label>
                    </div>
                <div>
                <label>
                    Message:
                        <textarea value="{state.message}"/>
            </label>
                    <input type="submit" value="enviar"/>
                
                </div>
                </form>
               
        )
    }
}
export default MyForm;
