import React, { Component } from 'react';
import './App.css';
import Course from './componentes/Course';
class App extends Component{

  constructor(props){
    super(props);
    this.state={
      courses:[{
        name:'java Script',
        category:'web',
        image:'https://www.google.com/url?sa=i&url=https%3A%2F%2Fblog.geekhunter.com.br%2Fum-guia-para-usar-react-js%2F&psig=AOvVaw3BpboicrMjlcZtzzEacazd&ust=1585755325652000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCIDrpImFxegCFQAAAAAdAAAAABAP'

      }
    ]
    }
  }


  render(){
    const{ state }=this;
    const newLocal = state.courses.map(course => <Course course={course} />);
    return (
      <div className="App">
        <ul className="course-list">
          {
             newLocal
          }
        </ul>
      </div>
    );
  }
}

export default App;
