import React, { Component } from 'react';
import './App.css';
import Course from './componentes/Course';
class App extends Component{

  constructor(props){
    super(props);
    this.state={
      courses:[{
        name:'java Script',
        category:'web',
        imagem:'https://upload.wikimedia.org/wikipedia/commons/thumb/a/a7/React-icon.svg/1200px-React-icon.svg.pnghttps://www.google.com/url?sa=i&url=https%3A%2F%2Fpt.wikipedia.org%2Fwiki%2FJavaScript&psig=AOvVaw2VXri-1EwldeRUvuU7MUPu&ust=1585754947495000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCNDvptWDxegCFQAAAAAdAAAAABAD'
      }
    ]
    }
  }


  render(){
    const{ state }=this;
    return (
      <div className="App">
        <ul className="course-list">
          {
             state.courses.map(course => <Course course={course} onRemove={this.remove} />)
          }
        </ul>
      </div>
    );
  }
}

export default App;
