import React, { Component } from 'react';
import './App.css';
import Course from './componentes/Course';
import Myform from './MyForm';
import TextCounter from './TextCounter';
import NewCourseForm from './componentes/NewCourseForm';
class App extends Component{
  static defaultProps={
    onSubmit
  }
  constructor(props){
    super(props);
    this.state={
      courses:[{
        id:1,
        name:'react',
        category:'web',
        image:'https://upload.wikimedia.org/wikipedia/commons/thumb/a/a7/React-icon.svg/1200px-React-icon.svg.png'

      }
    ]
    }
    this.add = this.add.bind(this);
    this.remove = this.remove.bind(this);
  }
  add(course){
    const courses = this.state,
    newCourse = Object.assign({},course,{id:(Date.now())});
    courses.push(course);
    this.setState({course});
  }
remove(courseId){
  const { courses } = this.state,
  courseIndex = courses.findIndex(course => courses.id == courseId);
  courses.splice(courseIndex, 1);
  this.setState({courses});
}



  render(){
    const{ state }=this;
    return (

      <div className="App">
        <NewCourseForm  onSubmit={this.add}/>
        <ul className="course-list">
          {
             state.courses.map(course => <Course course={course} onRemove={this.remove} />)
          }
        </ul>
      </div>
    );
  }
}

export default App;
